-- CREACION DE TABLAS
CREATE TABLE tipo_cliente(
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    tipo_cliente NVARHCAR(100) NOT NULL
);

CREATE TABLE tipo_tarjeta(
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    tipo_tarjeta NVARCHAR(100) NOT NULL
);

CREATE TABLE marca_tarjeta(
    id INTEGER PRIMARY KEY AUTOINCREMENT, 
    marca_tarjeta NVARCHAR(100) NOT NULL
);

CREATE TABLE tarjeta(
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    numero VARCHAR(20) UNIQUE CHECK (LENGTH(numero) <= 20),
    cvv INT NOT NULL,
    fecha_otorgamiento DATETIME NOT NULL,
    fecha_exipracion DATETIME NOT NULL,
    tipo_tarjeta_id INT NOT NULL,
    marca_tarjeta_id INT NOT NULL,

    FOREIGN KEY (marca_tarjeta_id) REFERENCES marca_tarjeta(id),
    FOREIGN KEY (tipo_tarjeta_id) REFERENCES tipo_tarjeta(id)
);

CREATE TABLE direccion(
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    calle VARCHAR(100) NOT NULL,
    ciudad VARCHAR(100) NOT NULL,
    codigo_postal VARCHAR(100) NOT NULL,
    pais VARCHAR(100) NOT NULL
);

CREATE TABLE tipo_cuenta(
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    tipo_cuenta VARCHAR(100) NOT NULL
);

CREATE TABLE clientes_direccion(
    customer_id INTEGER ,
    id_dirrecion INTEGER,

    FOREIGN KEY (customer_id)
        REFERENCES cliente(customer_id)
            ON DELETE NO ACTION
            ON UPDATE NO ACTION,

    FOREIGN KEY (id_dirrecion)
        REFERENCES direccion(id)
            ON DELETE NO ACTION
            ON UPDATE NO ACTION
);

CREATE TABLE empleado_direccion(
    employee_id INTEGER ,
    id_dirrecion INTEGER,

    FOREIGN KEY (employee_id)
        REFERENCES empleado(employee_id)
            ON DELETE NO ACTION
            ON UPDATE NO ACTION,

    FOREIGN KEY (id_dirrecion)
        REFERENCES direccion(id)
            ON DELETE NO ACTION
            ON UPDATE NO ACTION
);

